    ns1:relation <http://www.iana.org/assignments/relation/describes> ;
    rec:hasVariable _:Nfb6c12a4aacd4804ad3667b9c06be72c ;
    rec:uriTemplate "https://geo.woudc.org/ows?crs={crs}&bbox={bbox}"^^xsd:string .

